import { useEffect, useState } from "react";
import { Camera, Video, Upload, Trash2, X } from "lucide-react";
import axios from "axios";
import Select from "react-select";

function ReportForm({ editingReportId, onSubmitSuccess }) {
  const [products, setProducts] = useState([
    { name: "", quantity: "", reason: "", files: [], previews: [] },
  ]);
  const [containers, setContainers] = useState([]);
  const [selectedContainer, setSelectedContainer] = useState("");

  useEffect(() => {
    const fetchContainers = async () => {
      try {
        const response = await axios.get(
          `http://${process.env.REACT_APP_NETWORK}:${process.env.REACT_APP_PORT}/getAllContainers`,
          {
            headers: {
              Authorization: `Bearer ${localStorage.getItem("token")}`,
            },
          }
        );

        const data = JSON.parse(response.data).data;

        if (Array.isArray(data)) {
          setContainers(data);
        } else {
          console.error("Unexpected container format", data);
          setContainers([]);
        }
      } catch (err) {
        console.error("Error fetching containers:", err);
      }
    };

    fetchContainers();

    return () => {
      // Cleanup object URLs on unmount
      products.forEach((product) => {
        product?.previews?.forEach((preview) =>
          URL.revokeObjectURL(preview.url)
        );
      });
    };
  }, []);

  useEffect(() => {
    if (!editingReportId) return;

    const loadReportData = async () => {
      try {
        const response = await axios.get(
          `http://${process.env.REACT_APP_NETWORK}:${process.env.REACT_APP_PORT}/getDamageReportById/${editingReportId}`,
          {
            headers: {
              Authorization: `Bearer ${localStorage.getItem("token")}`,
            },
          }
        );

        const { container_id, products } = response.data;
        setSelectedContainer(container_id);
        setProducts(products.map(p => ({
          name: p.name,
          quantity: p.quantity,
          reason: p.reason,
          files: [], // user may upload new files
          previews: (p.images || []).map(url => ({
            url: `http://your-cdn.com/${url}`, // adjust if needed
            type: url.endsWith(".mp4") ? "video" : "image",
          }))
        })));
      } catch (err) {
        console.error("Failed to load report for editing:", err);
      }
    };

    loadReportData();
  }, [editingReportId]);


  const handleChange = (index, field, value) => {
    const updated = [...products];
    updated[index][field] = value;
    setProducts(updated);
  };

  const handleRemoveFile = (productIndex, fileIndex) => {
    const updated = [...products];
    
    // Revoke preview URL
    const removedPreview = updated[productIndex].previews[fileIndex];
    URL.revokeObjectURL(removedPreview.url);

    updated[productIndex].files.splice(fileIndex, 1);
    updated[productIndex].previews.splice(fileIndex, 1);

    setProducts(updated);
  };

  const handleFileChange = (index, files) => {
    const fileArray = Array.from(files);
    const updated = [...products];
    updated[index].files = fileArray;
    updated[index].previews = fileArray.map((file) => ({
      url: URL.createObjectURL(file),
      type: file.type.startsWith("image") ? "image" : "video",
    }));
    setProducts(updated);
  };

  const addProduct = () => {
    setProducts([
      ...products,
      { name: "", quantity: "", reason: "", files: [], previews: [] },
    ]);
  };

  const removeProduct = (index) => {
    // Clean up previews
    products[index].previews?.forEach((preview) =>
      URL.revokeObjectURL(preview.url)
    );

    setProducts(products.filter((_, i) => i !== index));
  };

  const submitDamageReport = async (containerId, products) => {
    if (!containerId) throw new Error("Container ID is required");

    const formData = new FormData();

    const dataPayload = {
      container_id: containerId,
      products: products.map((product) => ({
        name: product.name,
        quantity: product.quantity,
        reason: product.reason,
        files: product.files.map(() => "placeholder"),
      })),
    };

    formData.append("data", JSON.stringify(dataPayload));

    products.forEach((product) => {
      product.files.forEach((file) => {
        formData.append("files", file);
      });
    });

    const url = `http://${process.env.REACT_APP_NETWORK}:${process.env.REACT_APP_PORT}/submitDamagedProducts`;

    const headers = {
      Authorization: `Bearer ${localStorage.getItem("token")}`,
    };

    return axios.post(url, formData, { headers });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!selectedContainer) {
      alert("Please select a container.");
      return;
    }

    try {
      await submitDamageReport(selectedContainer, products);
      alert("Damage report submitted!");
      if (onSubmitSuccess) onSubmitSuccess();
    } catch (error) {
      console.error("Submission failed:", error);
      alert("Failed to submit damage report.");
    }
  };

  return (
    <form onSubmit={handleSubmit} className="max-w-5xl mx-auto p-4 space-y-6">
      <h2 className="text-3xl font-bold text-red-600 text-center">
        Damaged Product Report
      </h2>

      {/* Container Selector */}
      <div>
        <label className="block font-medium mb-2">Select Container</label>
        <Select
          options={containers.map(([id, number]) => ({
            value: id,
            label: number,
          }))}
          value={
            selectedContainer
              ? {
                  value: selectedContainer,
                  label: containers.find(([id]) => id === selectedContainer)?.[1],
                }
              : null
          }
          onChange={(option) => setSelectedContainer(option?.value)}
          isClearable
          placeholder="Select or type to search"
          className="react-select-container"
          classNamePrefix="react-select"
        />
      </div>

      {/* Product Inputs */}
      {products.map((product, index) => (
        <div
          key={index}
          className="bg-red-50 border border-red-200 rounded-xl p-4 space-y-4 shadow-sm"
        >
          <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
            <input
              type="text"
              placeholder="Product Name"
              value={product.name}
              onChange={(e) => handleChange(index, "name", e.target.value)}
              className="border px-3 py-2 rounded"
              required
            />
            <input
              type="number"
              placeholder="Quantity"
              value={product.quantity}
              onChange={(e) => handleChange(index, "quantity", e.target.value)}
              className="border px-3 py-2 rounded"
              required
            />
            <input
              type="text"
              placeholder="Reason"
              value={product.reason}
              onChange={(e) => handleChange(index, "reason", e.target.value)}
              className="border px-3 py-2 rounded"
            />
          </div>

          {/* File Upload */}
          <div className="grid grid-cols-2 sm:grid-cols-3 md:grid-cols-4 gap-4">
            <div className="block sm:hidden">
              <label className="flex items-center gap-2 cursor-pointer">
                <Camera className="w-5 h-5" />
                <span>Take Photo</span>
                <input
                  type="file"
                  accept="image/*"
                  capture="environment"
                  onChange={(e) => handleFileChange(index, e.target.files)}
                  className="hidden"
                />
              </label>
            </div>

            <div className="block sm:hidden">
              <label className="flex items-center gap-2 cursor-pointer">
                <Video className="w-5 h-5" />
                <span>Take Video</span>
                <input
                  type="file"
                  accept="video/*"
                  capture="environment"
                  onChange={(e) => handleFileChange(index, e.target.files)}
                  className="hidden"
                />
              </label>
            </div>

            <div className="col-span-2 sm:col-span-1">
              <label className="flex items-center gap-2 cursor-pointer">
                <Upload className="w-5 h-5" />
                <span>Select Files</span>
                <input
                  type="file"
                  accept="image/*,video/*"
                  multiple
                  onChange={(e) => handleFileChange(index, e.target.files)}
                  className="hidden"
                />
              </label>
              <p className="text-sm text-gray-500">
                {product.files.length} file(s) selected
              </p>
            </div>
          </div>

          {/* File Previews */}
          {product.previews && product.previews.length > 0 && (
            <div className="grid grid-cols-2 sm:grid-cols-3 gap-4 mt-4">
              {product.previews.map((preview, i) => (
                <div
                  key={i}
                  className="relative border rounded overflow-hidden group"
                >
                  {preview.type === "image" ? (
                    <img
                      src={preview.url}
                      alt={`preview-${i}`}
                      className="w-full h-32 object-cover"
                    />
                  ) : (
                    <video
                      src={preview.url}
                      controls
                      className="w-full h-32 object-cover"
                    />
                  )}

                  <button
                    type="button"
                    onClick={() => handleRemoveFile(index, i)}
                    className="absolute top-1 right-1 bg-white rounded-full p-1 text-red-600 shadow hover:bg-red-100"
                    title="Remove file"
                  >
                    <X className="w-4 h-4" />
                  </button>
                </div>
              ))}
            </div>
          )}


          {/* Remove Product Button */}
          <div className="flex justify-end">
            <button
              type="button"
              onClick={() => removeProduct(index)}
              className="flex items-center gap-1 text-red-600 hover:underline"
            >
              <Trash2 className="w-4 h-4" />
              Remove
            </button>
          </div>
        </div>
      ))}

      {/* Action Buttons */}
      <div className="flex flex-col sm:flex-row justify-between gap-4 pt-4">
        <button
          type="button"
          onClick={addProduct}
          className="bg-blue-600 text-white px-4 py-2 rounded shadow hover:bg-blue-700"
        >
          + Add Product
        </button>
        <button
          type="submit"
          className="bg-green-600 text-white px-6 py-2 rounded shadow hover:bg-green-700"
        >
          Submit Damage Report
        </button>
      </div>
    </form>
  );
}

export default ReportForm;
