import { Routes, Route, useLocation } from "react-router-dom";
import { useState } from "react";
import Sidebar from './MenuPanel/Menu';
import ContainerEntry from './Pages/DataEntry/ConatinerEntry';
import LoginPage from './Pages/Login/Login';
import PrivateRoute from './PrivateRoute';
import Dashboard from './Pages/Dashboard/dashboard';
import Report from "./Pages/Report/Report";
import { Menu } from "lucide-react";

export default function MainPage() {
  const location = useLocation();
  const hideSidebar = location.pathname === "/";
  const [sidebarOpen, setSidebarOpen] = useState(true);

  return (
    <div className="flex min-h-screen">
      {!hideSidebar && (
        <div className={`${sidebarOpen ? "block" : "hidden"} md:block`}>
          <Sidebar />
        </div>
      )}

      <div className="flex-1">
        {/* Navbar with toggle button */}
        {!hideSidebar && (
          <div className="bg-slate-900 text-white p-4 flex items-center justify-between md:justify-end">
            <button
              className="md:hidden text-white"
              onClick={() => setSidebarOpen(!sidebarOpen)}
            >
              <Menu size={24} />
            </button>
            <h1 className="hidden md:block text-lg font-semibold">Welcome</h1>
          </div>
        )}

        {/* Main routes */}
        <Routes>
          <Route path="/" element={<LoginPage />} />

          <Route
            path="/dashboard"
            element={
              <PrivateRoute>
                <Dashboard />
              </PrivateRoute>
            }
          />

          <Route
            path="/viewContainer"
            element={
              <PrivateRoute>
                <ContainerEntry />
              </PrivateRoute>
            }
          />

          <Route
            path="/Report"
            element={
              <PrivateRoute>
                <Report />
              </PrivateRoute>
            }
          />
        </Routes>
      </div>
    </div>
  );
}
