import React, { useState } from "react";
import { Link } from "react-router-dom";
import {
  ChevronDown,
  LayoutDashboard,
  FileText,
  Layers,
  Container,
} from "lucide-react";

export default function Sidebar() {
  const [openMenus, setOpenMenus] = useState({
    dashboard: false,
    assets: false,
    fuel: false,
  });

  const toggleMenu = (menu) => {
    setOpenMenus((prev) => ({
      ...prev,
      [menu]: !prev[menu],
    }));
  };

  return (
    <div className="w-64 min-h-screen bg-slate-950 text-white flex flex-col justify-between">
      <div className="p-4 space-y-2">
        <nav className="flex flex-col gap-2">
          {/* Dashboard Collapsible */}
          <button
            className="w-full flex justify-between items-center px-3 py-2 hover:bg-slate-800 rounded"
            onClick={() => toggleMenu("dashboard")}
          >
            <div className="flex items-center gap-2">
              <LayoutDashboard size={1} />
              <span className="text-sm">Dashboard</span>
            </div>
            <ChevronDown
              size={16}
              className={`transition-transform duration-300 ${openMenus.dashboard ? "rotate-180" : ""}`}
            />
          </button>

          <div
            className={`ml-8 flex flex-col overflow-hidden transition-all duration-300 ${openMenus.dashboard ? "max-h-40" : "max-h-0"}`}
          >
            <Link to="/dashboard" className="text-sm hover:text-blue-400 py-1">Overview</Link>
            <Link to="/dashboard/analytics" className="text-sm hover:text-blue-400 py-1">Analytics</Link>
            <Link to="/dashboard/saas" className="text-sm hover:text-blue-400 py-1">SaaS</Link>
          </div>

          {/* Static Menu Item */}
          <Link
            to="/viewContainer"
            className="flex items-center gap-2 px-3 py-2 hover:bg-slate-800 rounded"
          >
            <Container size={18} />
            <span className="text-sm">View Container</span>
          </Link>
        </nav>
      </div>

      {/* User Profile */}
      <div className="px-4 py-3 border-t border-slate-800 flex items-center gap-3 bg-slate-950">
        <img
          src="/user.jpg"
          alt="User"
          className="w-10 h-10 rounded-full object-cover"
        />
        <div>
          <p className="text-sm font-medium">John Doe</p>
          <p className="text-xs text-slate-400">Logged in</p>
        </div>
      </div>
    </div>
  );
}
