import { useEffect, useState } from "react";
import axios from "axios";

function ReportForm({ onSubmitSuccess }) {
  const [products, setProducts] = useState([
    { name: "", quantity: "", reason: "", files: [] },
  ]);
  const [containers, setContainers] = useState([]);
  const [selectedContainer, setSelectedContainer] = useState("");

  useEffect(() => {
    const fetchContainers = async () => {
      try {
        const response = await axios.get(
          `http://${process.env.REACT_APP_NETWORK}:${process.env.REACT_APP_PORT}/getAllContainers`,
          {
            headers: {
              Authorization: `Bearer ${localStorage.getItem("token")}`,
            },
          }
        );
        setContainers(response.data);
      } catch (err) {
        console.error("Error fetching containers:", err);
      }
    };
    fetchContainers();
  }, []);

  const handleChange = (index, field, value) => {
    const updated = [...products];
    updated[index][field] = value;
    setProducts(updated);
  };

  const handleFileChange = (index, files) => {
    const updated = [...products];
    updated[index].files = Array.from(files);
    setProducts(updated);
  };

  const addProduct = () => {
    setProducts([...products, { name: "", quantity: "", reason: "", files: [] }]);
  };

  const removeProduct = (index) => {
    setProducts(products.filter((_, i) => i !== index));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!selectedContainer) {
      alert("Please select a container first.");
      return;
    }

    const formData = new FormData();
    formData.append("container_id", selectedContainer);

    products.forEach((product, i) => {
      formData.append(`products[${i}][name]`, product.name);
      formData.append(`products[${i}][quantity]`, product.quantity);
      formData.append(`products[${i}][reason]`, product.reason);
      product.files.forEach((file) => {
        formData.append(`products[${i}][files]`, file);
      });
    });

    try {
      await axios.post(
        `http://${process.env.REACT_APP_NETWORK}:${process.env.REACT_APP_PORT}/submitDamagedProducts`,
        formData,
        {
          headers: {
            "Content-Type": "multipart/form-data",
            Authorization: `Bearer ${localStorage.getItem("token")}`,
          },
        }
      );
      alert("Damage report submitted!");
      if (onSubmitSuccess) onSubmitSuccess();
    } catch (error) {
      console.error("Submission failed:", error);
      alert("Failed to submit damage report.");
    }
  };

  return (
    <form
      onSubmit={handleSubmit}
      className="max-w-5xl mx-auto px-4 py-8 space-y-6"
    >
      <h2 className="text-3xl font-bold text-red-600 mb-6 text-center">
        Damaged Product Report
      </h2>

      {/* Container Select */}
      <div className="space-y-2">
        <label className="block font-medium text-lg">Select Container</label>
        <select
          value={selectedContainer}
          onChange={(e) => setSelectedContainer(e.target.value)}
          className="w-full border border-gray-300 px-4 py-2 rounded shadow-sm"
          required
        >
          <option value="">-- Select a container --</option>
          {containers.map((c) => (
            <option key={c.container_id} value={c.container_id}>
              {c.container_no}
            </option>
          ))}
        </select>
      </div>

      {/* Product Inputs */}
      {products.map((product, index) => (
        <div
          key={index}
          className="bg-red-50 rounded-xl p-6 shadow-sm space-y-4 border border-red-200"
        >
          <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
            <input
              type="text"
              placeholder="Product Name"
              value={product.name}
              onChange={(e) => handleChange(index, "name", e.target.value)}
              className="border px-4 py-2 rounded w-full"
              required
            />
            <input
              type="number"
              placeholder="Quantity"
              value={product.quantity}
              onChange={(e) => handleChange(index, "quantity", e.target.value)}
              className="border px-4 py-2 rounded w-full"
              required
            />
            <input
              type="text"
              placeholder="Reason"
              value={product.reason}
              onChange={(e) => handleChange(index, "reason", e.target.value)}
              className="border px-4 py-2 rounded w-full"
            />
          </div>

          {/* File Upload Section */}
          <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
            {/* Take Photo */}
            <div>
              <label className="block font-medium mb-1">Take Photo</label>
              <input
                type="file"
                accept="image/*"
                capture="environment"
                onChange={(e) => handleFileChange(index, e.target.files)}
                className="block w-full border rounded px-2 py-1"
              />
            </div>

            {/* Take Video */}
            <div>
              <label className="block font-medium mb-1">Take Video</label>
              <input
                type="file"
                accept="video/*"
                capture="environment"
                onChange={(e) => handleFileChange(index, e.target.files)}
                className="block w-full border rounded px-2 py-1"
              />
            </div>

            {/* Upload from Storage */}
            <div>
              <label className="block font-medium mb-1">Upload from Storage</label>
              <input
                type="file"
                accept="image/*,video/*"
                multiple
                onChange={(e) => handleFileChange(index, e.target.files)}
                className="block w-full border rounded px-2 py-1"
              />
            </div>
          </div>

          {/* Remove Product */}
          <div className="flex justify-end">
            <button
              type="button"
              onClick={() => removeProduct(index)}
              className="text-red-600 font-semibold hover:underline"
            >
              ✕ Remove
            </button>
          </div>
        </div>
      ))}

      {/* Buttons */}
      <div className="flex flex-col sm:flex-row justify-between gap-4 pt-4">
        <button
          type="button"
          onClick={addProduct}
          className="bg-blue-600 text-white px-4 py-2 rounded shadow hover:bg-blue-700"
        >
          + Add Product
        </button>
        <button
          type="submit"
          className="bg-green-600 text-white px-6 py-2 rounded shadow hover:bg-green-700"
        >
          Submit Damage Report
        </button>
      </div>
    </form>
  );
}

export default ReportForm;
