import { useState } from "react";

function ContainerEntryForm() {
  const [formData, setFormData] = useState({
    consignee: "",
    poNo: "",
    supplier: "",
    material: "",
    arrivalDate: "",
    arrivalTime: "",
    docs: "",
    tax: "",
    type: "",
    containerNo: "",
    inBound: "",
    emptyDate: "",
    outBound: "",
    unloadedAtDock: "",
    emptyAt: "",
    note: ""
  });

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    console.log(formData);
  };

  return (
    <div className="max-w-7xl mx-auto bg-white p-4 rounded shadow border border-gray-200">
      <form onSubmit={handleSubmit} className="grid grid-cols-1 md:grid-cols-3 gap-3 text-sm">
        <h2 className="col-span-full text-lg font-semibold mb-2">Container Entry</h2>

        {[
          { label: "Consignee", name: "consignee", type: "select" },
          { label: "PO No", name: "poNo", type: "text" },
          { label: "Supplier", name: "supplier", type: "select" },
          { label: "Material", name: "material", type: "text" },
          { label: "Arrival Date", name: "arrivalDate", type: "date" },
          { label: "Arrival Time", name: "arrivalTime", type: "time" },
          { label: "Docs", name: "docs", type: "text" },
          { label: "Tax", name: "tax", type: "text" },
          { label: "Type", name: "type", type: "text" },
          { label: "Container No", name: "containerNo", type: "text" },
          { label: "In Bound", name: "inBound", type: "select" },
          { label: "Empty Date", name: "emptyDate", type: "date" },
          { label: "Out Bound", name: "outBound", type: "select" },
          { label: "Unloaded @ Dock", name: "unloadedAtDock", type: "select" },
          { label: "Empty At", name: "emptyAt", type: "select" },
        ].map(({ label, name, type }) => (
          <div key={name}>
            <label className="block mb-1">{label}</label>
            {type === "select" ? (
              <select
                name={name}
                value={formData[name]}
                onChange={handleChange}
                className="w-full border rounded px-2 py-1"
              >
                <option value="">Select {label}</option>
              </select>
            ) : (
              <input
                type={type}
                name={name}
                value={formData[name]}
                onChange={handleChange}
                className="w-full border rounded px-2 py-1"
              />
            )}
          </div>
        ))}

        <div className="col-span-full">
          <label className="block mb-1">Personal Note</label>
          <textarea
            name="note"
            value={formData.note}
            onChange={handleChange}
            className="w-full border rounded px-2 py-1"
            rows="2"
          />
        </div>

        <div className="col-span-full">
          <button
            type="submit"
            className="w-full bg-blue-600 text-white py-1.5 rounded hover:bg-blue-700"
          >
            Submit
          </button>
        </div>
      </form>
    </div>
  );
}

export default ContainerEntryForm;
