import { Routes, Route, useLocation } from "react-router-dom";
import { useState, useEffect } from "react";
import Sidebar from './MenuPanel/Menu';
import ContainerEntry from './Pages/DataEntry/ConatinerEntry';
import LoginPage from './Pages/Login/Login';
import PrivateRoute from './PrivateRoute';
import Dashboard from './Pages/Dashboard/dashboard';
import Report from "./Pages/Report/Report";

export default function MainPage() {
  const location = useLocation();
  const isLoginPage = location.pathname === "/";
  const [sidebarVisible, setSidebarVisible] = useState(false);

  // Prevent background scroll when mobile sidebar is open
  useEffect(() => {
    document.body.classList.toggle("overflow-hidden", sidebarVisible);
  }, [sidebarVisible]);

  return (
    <div className="flex min-h-screen relative bg-slate-100">

      {/* Sidebar */}
      {!isLoginPage && (
        <>
          {/* Mobile sidebar (no animation, just show/hide) */}
          <div className={`md:hidden ${sidebarVisible ? "block" : "hidden"} fixed  left-0 w-64 bg-slate-950 z-50`}>
            <Sidebar onLinkClick={() => setSidebarVisible(false)} />
          </div>

          {/* Desktop sidebar (always visible) */}
          <aside className="hidden md:block w-64 bg-slate-950 text-white min-h-screen">
            <Sidebar />
          </aside>

          {/* Overlay for mobile */}
          {sidebarVisible && (
            <div
              className="fixed inset-0 bg-black bg-opacity-40 z-40 md:hidden"
              onClick={() => setSidebarVisible(false)}
            />
          )}
        </>
      )}

      {/* Main content */}
      <main className={`flex-1 ${!isLoginPage ? "md:ml-64" : ""} relative`}>
        
        {/* Mobile Hamburger Button */}
        {!isLoginPage && (
          <button
            className="md:hidden fixed top-4 left-4 z-50 bg-slate-800 text-white p-2 rounded"
            onClick={() => setSidebarVisible(!sidebarVisible)}
            aria-label="Toggle Sidebar"
          >
            â˜°
          </button>
        )}

        {/* Page Content */}
        <div className="p-4 pt-16 md:pt-4">
          <Routes>
            <Route path="/" element={<LoginPage />} />
            <Route path="/dashboard" element={<PrivateRoute><Dashboard /></PrivateRoute>} />
            <Route path="/viewContainer" element={<PrivateRoute><ContainerEntry /></PrivateRoute>} />
            <Route path="/Report" element={<PrivateRoute><Report /></PrivateRoute>} />
          </Routes>
        </div>
      </main>
    </div>
  );
}
