import { useState, useEffect } from "react";

function ContainerEntryForm() {
  const [formData, setFormData] = useState({
    consignee: "",
    poNo: "",
    supplier: "",
    material: "",
    arrivalDate: "",
    arrivalTime: "",
    tax: "",
    type: "",
    containerNo: "",
    inBound: "",
    emptyDate: "",
    outBound: "",
    unloadedAtDock: "",
    emptyAt: "",
    note: ""
  });

  // documents: array of { file: File, previewUrl: string|null }
  const [documents, setDocuments] = useState([]);

  const handleChange = (e) => {
    setFormData((prev) => ({ ...prev, [e.target.name]: e.target.value }));
  };

  // Generate preview URL for supported files
  const generatePreview = (file) => {
    if (!file) return null;
    const supportedTypes = ["application/pdf", "image/jpeg", "image/png", "image/gif"];
    if (supportedTypes.includes(file.type)) {
      return URL.createObjectURL(file);
    }
    return null;
  };

  const handleDocumentChange = (e, index) => {
    const file = e.target.files[0] || null;
    setDocuments((prevDocs) => {
      // Revoke previous preview URL if exists
      if (prevDocs[index]?.previewUrl) {
        URL.revokeObjectURL(prevDocs[index].previewUrl);
      }

      const newDoc = {
        file,
        previewUrl: generatePreview(file),
      };

      const updatedDocs = [...prevDocs];
      updatedDocs[index] = newDoc;
      return updatedDocs;
    });
  };

  const addDocumentInput = () => {
    setDocuments((prevDocs) => [...prevDocs, { file: null, previewUrl: null }]);
  };

  const removeDocumentInput = (index) => {
    setDocuments((prevDocs) => {
      if (prevDocs[index]?.previewUrl) {
        URL.revokeObjectURL(prevDocs[index].previewUrl);
      }
      return prevDocs.filter((_, i) => i !== index);
    });
  };

  // Clean up URLs on unmount
  useEffect(() => {
    return () => {
      documents.forEach((doc) => {
        if (doc.previewUrl) URL.revokeObjectURL(doc.previewUrl);
      });
    };
  }, [documents]);

  const handleSubmit = (e) => {
    e.preventDefault();
    console.log("Form Data:", formData);
    console.log("Documents:", documents.map(doc => doc.file));
    // Handle upload logic here
  };

  return (
    <div className="max-w-7xl mx-auto bg-white p-4 rounded shadow border border-gray-200">
      <form onSubmit={handleSubmit} className="grid grid-cols-1 md:grid-cols-3 gap-3 text-sm">
        <h2 className="col-span-full text-lg font-semibold mb-2">Container Entry</h2>

        {[
          { label: "Consignee", name: "consignee", type: "select" },
          { label: "PO No", name: "poNo", type: "text" },
          { label: "Supplier", name: "supplier", type: "select" },
          { label: "Material", name: "material", type: "textarea" },
          { label: "Arrival Date", name: "arrivalDate", type: "date" },
          { label: "Arrival Time", name: "arrivalTime", type: "time" },
          { label: "Tax", name: "tax", type: "text" },
          { label: "Type", name: "type", type: "text" },
          { label: "Container No", name: "containerNo", type: "text" },
          { label: "In Bound", name: "inBound", type: "select" },
          { label: "Empty Date", name: "emptyDate", type: "date" },
          { label: "Out Bound", name: "outBound", type: "select" },
          { label: "Unloaded @ Dock", name: "unloadedAtDock", type: "select" },
          { label: "Empty At", name: "emptyAt", type: "select" },
        ].map(({ label, name, type }) => (
          <div key={name}>
            <label htmlFor={name} className="block mb-1">{label}</label>
            {type === "select" ? (
              <select
                id={name}
                name={name}
                value={formData[name]}
                onChange={handleChange}
                className="w-full border rounded px-2 py-1"
              >
                <option value="">Select {label}</option>
              </select>
            ) : type === "textarea" ? (
              <textarea
                id={name}
                name={name}
                value={formData[name]}
                onChange={handleChange}
                className="w-full border rounded px-2 py-1"
                rows="2"
              />
            ) : (
              <input
                id={name}
                type={type}
                name={name}
                value={formData[name]}
                onChange={handleChange}
                className="w-full border rounded px-2 py-1"
              />
            )}
          </div>
        ))}

        {/* Documents Upload Section */}
        <div className="col-span-full">
          <label className="block mb-1 font-semibold">Documents (Packing Lists, etc.)</label>
          {documents.map((doc, idx) => (
            <div key={idx} className="mb-4 border p-2 rounded">
              <div className="flex items-center space-x-2 mb-2">
                <input
                  type="file"
                  onChange={(e) => handleDocumentChange(e, idx)}
                  className="border rounded px-2 py-1 flex-grow"
                  accept=".pdf,image/*"
                />
                <button
                  type="button"
                  onClick={() => removeDocumentInput(idx)}
                  className="text-red-600 hover:text-red-800"
                  aria-label={`Remove document ${idx + 1}`}
                >
                  Remove
                </button>
              </div>

              {/* Preview area */}
              {doc.file && doc.previewUrl ? (
                doc.file.type === "application/pdf" ? (
                  <iframe
                    src={doc.previewUrl}
                    title={`Preview of ${doc.file.name}`}
                    className="w-full h-48 border rounded"
                  />
                ) : (
                  <img
                    src={doc.previewUrl}
                    alt={doc.file.name}
                    className="max-w-full max-h-48 object-contain border rounded"
                  />
                )
              ) : doc.file ? (
                <p className="text-gray-700 truncate">{doc.file.name} (No preview available)</p>
              ) : null}
            </div>
          ))}
          <button
            type="button"
            onClick={addDocumentInput}
            className="bg-green-600 text-white px-3 py-1 rounded hover:bg-green-700"
          >
            + Add Document
          </button>
        </div>

        {/* Personal Note */}
        <div className="col-span-full">
          <label htmlFor="note" className="block mb-1">Personal Note</label>
          <textarea
            id="note"
            name="note"
            value={formData.note}
            onChange={handleChange}
            className="w-full border rounded px-2 py-1"
            rows="2"
          />
        </div>

        <div className="col-span-full">
          <button
            type="submit"
            className="w-full bg-blue-600 text-white py-1.5 rounded hover:bg-blue-700"
          >
            Submit
          </button>
        </div>
      </form>
    </div>
  );
}

export default ContainerEntryForm;
