import { Routes, Route, useLocation } from "react-router-dom";
import { useState, useEffect } from "react";
import Sidebar from './MenuPanel/Menu';
import ContainerEntry from './Pages/DataEntry/ConatinerEntry';
import LoginPage from './Pages/Login/Login';
import PrivateRoute from './PrivateRoute';
import Dashboard from './Pages/Dashboard/dashboard';
import Report from "./Pages/Report/Report";

export default function MainPage() {
  const location = useLocation();
  const hideSidebar = location.pathname === "/";
  const [sidebarOpen, setSidebarOpen] = useState(false);

  // Lock body scroll when sidebar is open on mobile
  useEffect(() => {
    if (sidebarOpen) {
      document.body.classList.add("overflow-hidden");
    } else {
      document.body.classList.remove("overflow-hidden");
    }
  }, [sidebarOpen]);

  return (
    <div className="flex min-h-screen relative">

      {/* Sidebar */}
      {!hideSidebar && (
        <>
          <div
            className={`fixed inset-y-0 left-0 z-50 w-64 transform bg-slate-950 text-white transition-transform duration-300 ease-in-out`}
            style={{ transform: sidebarOpen ? "translateX()" : "translateX(-100%)" }}
          >
            <Sidebar onLinkClick={() => setSidebarOpen(false)} />
          </div>

          
          {/* {sidebarOpen && (
            <div
              className="fixed inset-0 bg-black bg-opacity-40 z-40 md:hidden"
              onClick={() => setSidebarOpen(false)}
            />
          )} */}
        </>
      )}

      {/* Main content */}
      <div className={`flex-1 ${!hideSidebar ? "md:ml-64" : ""}`}>

        {/* Hamburger button (only visible when sidebar exists and we're not on login) */}
        {!hideSidebar && (
          <button
            className="md:hidden fixed top-4 left-4 z-50 bg-slate-800 text-white p-2 rounded-md"
            onClick={() => setSidebarOpen(!sidebarOpen)}
            aria-label="Toggle Sidebar"
          >
            â˜°
          </button>
        )}

        {/* Page Routes */}
        <div className="p-4 pt-16 md:pt-4">
          <Routes>
            <Route path="/" element={<LoginPage />} />

            <Route
              path="/dashboard"
              element={
                <PrivateRoute>
                  <Dashboard />
                </PrivateRoute>
              }
            />

            <Route
              path="/viewContainer"
              element={
                <PrivateRoute>
                  <ContainerEntry />
                </PrivateRoute>
              }
            />

            <Route
              path="/Report"
              element={
                <PrivateRoute>
                  <Report />
                </PrivateRoute>
              }
            />
          </Routes>
        </div>
      </div>
    </div>
  );
}
