import { useEffect, useState, useRef } from "react";
import axios from "axios";

function Report({ onSubmitSuccess }) {
  const [products, setProducts] = useState([
    { name: "", quantity: "", reason: "", media: [] }
  ]);
  const [containers, setContainers] = useState([]);
  const [selectedContainer, setSelectedContainer] = useState("");

  const cameraInputRefs = useRef([]);
  const fileInputRefs = useRef([]);

  useEffect(() => {
    const fetchContainers = async () => {
      try {
        const response = await axios.get(
          `http://${process.env.REACT_APP_NETWORK}:${process.env.REACT_APP_PORT}/getAllContainers`,
          {
            headers: {
              Authorization: `Bearer ${localStorage.getItem("token")}`,
            },
          }
        );
        setContainers(response.data);
      } catch (err) {
        console.error("Error fetching containers:", err);
      }
    };
    fetchContainers();
  }, []);

  const handleChange = (index, field, value) => {
    const updated = [...products];
    updated[index][field] = value;
    setProducts(updated);
  };

  const handleMediaChange = (index, files) => {
    const updated = [...products];
    updated[index].media = Array.from(files);
    setProducts(updated);
  };

  const addProduct = () => {
    setProducts([...products, { name: "", quantity: "", reason: "", media: [] }]);
  };

  const removeProduct = (index) => {
    setProducts(products.filter((_, i) => i !== index));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!selectedContainer) {
      alert("Please select a container first.");
      return;
    }

    const formData = new FormData();
    formData.append("container_id", selectedContainer);

    products.forEach((product, i) => {
      formData.append(`products[${i}][name]`, product.name);
      formData.append(`products[${i}][quantity]`, product.quantity);
      formData.append(`products[${i}][reason]`, product.reason);
      product.media.forEach((file) => {
        formData.append(`products[${i}][media]`, file);
      });
    });

    try {
      await axios.post(
        `http://${process.env.REACT_APP_NETWORK}:${process.env.REACT_APP_PORT}/submitDamagedProducts`,
        formData,
        {
          headers: {
            "Content-Type": "multipart/form-data",
            Authorization: `Bearer ${localStorage.getItem("token")}`,
          },
        }
      );
      alert("Damage report submitted!");
      if (onSubmitSuccess) onSubmitSuccess();
    } catch (error) {
      console.error("Submission failed:", error);
      alert("Failed to submit damage report.");
    }
  };

  return (
    <form onSubmit={handleSubmit} className="space-y-6 p-4 max-w-4xl mx-auto">
      <h2 className="text-2xl font-bold text-red-600 mb-4">Damaged Product Report</h2>

      {/* Container Select */}
      <div>
        <label className="block font-medium mb-1">Select Container</label>
        <select
          value={selectedContainer}
          onChange={(e) => setSelectedContainer(e.target.value)}
          className="w-full border px-3 py-2 rounded"
          required
        >
          <option value="">-- Select a container --</option>
          {containers.map((c) => (
            <option key={c.container_id} value={c.container_id}>
              {c.container_no}
            </option>
          ))}
        </select>
      </div>

      {/* Product Inputs */}
      {products.map((product, index) => (
        <div
          key={index}
          className="grid grid-cols-1 md:grid-cols-4 gap-4 items-start bg-red-50 p-4 rounded shadow-sm"
        >
          <input
            type="text"
            placeholder="Product Name"
            value={product.name}
            onChange={(e) => handleChange(index, "name", e.target.value)}
            className="border px-2 py-1 rounded"
            required
          />
          <input
            type="number"
            placeholder="Quantity"
            value={product.quantity}
            onChange={(e) => handleChange(index, "quantity", e.target.value)}
            className="border px-2 py-1 rounded"
            required
          />
          <input
            type="text"
            placeholder="Reason"
            value={product.reason}
            onChange={(e) => handleChange(index, "reason", e.target.value)}
            className="border px-2 py-1 rounded"
          />

          {/* Buttons for media */}
          <div className="flex flex-col space-y-2">
            <button
              type="button"
              className="bg-blue-600 text-white px-2 py-1 rounded hover:bg-blue-700"
              onClick={() => cameraInputRefs.current[index].click()}
            >
              üì∑ Take Photo/Video
            </button>
            <button
              type="button"
              className="bg-gray-600 text-white px-2 py-1 rounded hover:bg-gray-700"
              onClick={() => fileInputRefs.current[index].click()}
            >
              üìÅ Choose from Storage
            </button>

            {/* Hidden inputs */}
            <input
              type="file"
              accept="image/*,video/*"
              capture="environment"
              multiple
              ref={(el) => (cameraInputRefs.current[index] = el)}
              onChange={(e) => handleMediaChange(index, e.target.files)}
              style={{ display: "none" }}
            />
            <input
              type="file"
              accept="image/*,video/*"
              multiple
              ref={(el) => (fileInputRefs.current[index] = el)}
              onChange={(e) => handleMediaChange(index, e.target.files)}
              style={{ display: "none" }}
            />
          </div>

          <div className="col-span-full flex justify-end">
            <button
              type="button"
              onClick={() => removeProduct(index)}
              className="text-red-500 hover:text-red-700"
            >
              ‚úï Remove
            </button>
          </div>
        </div>
      ))}

      {/* Action Buttons */}
      <div className="flex justify-between items-center">
        <button
          type="button"
          onClick={addProduct}
          className="bg-blue-600 text-white px-3 py-1.5 rounded hover:bg-blue-700"
        >
          + Add Product
        </button>
        <button
          type="submit"
          className="bg-green-600 text-white px-4 py-2 rounded hover:bg-green-700"
        >
          Submit Damage Report
        </button>
      </div>
    </form>
  );
}

export default Report;
