import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import { LayoutDashboard, FileText, Container, ChevronDown } from 'lucide-react';
// import ReactTooltip from 'react-tooltip';
import ReactTooltip from 'react-tooltip';
function Sidebar() {
  const [isDashboardOpen, setIsDashboardOpen] = useState(false);

  return (
    <div className="group relative w-16 hover:w-64 transition-all duration-300 min-h-screen bg-slate-950 text-white flex flex-col justify-between overflow-hidden">
      {/* Navigation */}
      <div className="p-4 space-y-2">
        <nav className="flex flex-col gap-2">
          {/* Dashboard */}
          <div>
            <button
              onClick={() => setIsDashboardOpen(!isDashboardOpen)}
              data-tip="Dashboard"
              className="w-full flex items-center gap-3 px-3 py-2 rounded hover:bg-slate-800 transition"
            >
              <LayoutDashboard size={18} />
              <span className="text-sm group-hover:inline hidden">Dashboard</span>
              <ChevronDown
                className={`transition-transform ${isDashboardOpen ? 'rotate-180' : ''}`}
                size={16}
              />
            </button>
            {isDashboardOpen && (
              <div className="ml-6 space-y-1">
                <Link to="/dashboard" className="block text-sm hover:text-blue-400">
                  Overview
                </Link>
                <Link to="/dashboard/analytics" className="block text-sm hover:text-blue-400">
                  Analytics
                </Link>
                <Link to="/dashboard/saas" className="block text-sm hover:text-blue-400">
                  SaaS
                </Link>
              </div>
            )}
          </div>

          {/* Report */}
          <Link
            to="/report"
            data-tip="Report"
            className="w-full flex items-center gap-3 px-3 py-2 rounded hover:bg-slate-800 transition"
          >
            <FileText size={18} />
            <span className="text-sm group-hover:inline hidden">Report</span>
          </Link>

          {/* View Container */}
          <Link
            to="/viewContainer"
            data-tip="View Container"
            className="w-full flex items-center gap-3 px-3 py-2 rounded hover:bg-slate-800 transition"
          >
            <Container size={18} />
            <span className="text-sm group-hover:inline hidden">View Container</span>
          </Link>
        </nav>
      </div>

      {/* User Profile */}
      <div className="w-full px-4 py-3 border-t border-slate-800 flex items-center gap-3 bg-slate-950">
        <img
          src="/user.jpg"
          alt="User"
          className="w-10 h-10 rounded-full object-cover"
        />
        <div className="group-hover:block hidden">
          <p className="text-sm font-medium">John Doe</p>
          <p className="text-xs text-slate-400">Logged in</p>
        </div>
      </div>

      {/* Tooltip */}
      <ReactTooltip place="right" type="dark" effect="solid" />
    </div>
  );
}

export default Sidebar;
