import React, { useEffect, useState } from "react";
import { useAuth } from "../../../context/AuthContext";
import { useNavigate } from "react-router-dom";

const DashboardPage = () => {
  const { token, refreshAccessToken, logout } = useAuth();
  const navigate = useNavigate();
  const [userData, setUserData] = useState(null);
  const [error, setError] = useState("");
  const [loading, setLoading] = useState(true);  // Loading state

  useEffect(() => {
    // Redirect to login if no token
    if (!token) {
      navigate("/");
      return;
    }

    // Function to fetch user data
    const fetchUserData = async () => {
      try {
        setLoading(true);
        const response = await fetch(`http://${process.env.REACT_APP_NETWORK}:${process.env.REACT_APP_PORT}/users/me`, {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        });

        if (!response.ok) {
          // If token expired or invalid, attempt to refresh it
          if (response.status === 401) {
            const newToken = await refreshAccessToken();
            if (!newToken) {
              logout();
              navigate("/login");
              return;
            }
            // Retry fetching user data with the new token
            return fetchUserData();
          }
          throw new Error("Unauthorized or session expired");
        }

        const data = await response.json();
        setUserData(data);
      } catch (err) {
        setError(err.message);
      } finally {
        setLoading(false);
      }
    };

    fetchUserData();
  }, [token, navigate, refreshAccessToken, logout]);

  // Handle user logout
  const handleLogout = () => {
    logout();
    navigate("/");
  };

  return (
    <div className="min-h-screen flex flex-col items-center justify-center bg-gray-100 p-6">
      <div className="bg-white shadow-md rounded-lg p-6 w-full max-w-md">
        <h1 className="text-3xl font-bold text-center mb-6">Dashboard</h1>

        {/* Show error message */}
        {error && <p className="text-red-500 text-center">{error}</p>}

        {/* Show loading state or user data */}
        {loading ? (
          <p className="text-center text-gray-500">Loading user data...</p>
        ) : (
          userData && (
            <div className="space-y-4">
              <p><strong>Username:</strong> {userData.username}</p>
              <p><strong>Roles:</strong> {userData.roles?.join(", ") || "None"}</p>

              {/* Logout button */}
              <button
                onClick={handleLogout}
                className="w-full mt-4 bg-red-600 text-white py-2 rounded-lg hover:bg-red-700"
              >
                Logout
              </button>
            </div>
          )
        )}
      </div>
    </div>
  );
};

export default DashboardPage;
