import { useState } from "react";
import { ChevronDown, LayoutDashboard, FileText, Layers } from "lucide-react";
import "./Menu.css"; // Keep this only if you have custom styles

function Sidebar({ setActiveComponent }) {
  const [isDashboardOpen, setIsDashboardOpen] = useState(false);
  const [isPhysicalComponentsOpen, setIsPhysicalComponentsOpen] = useState(false);
  const [isFuelDetailsOpen, setIsFuelDetailsOpen] = useState(false); // Renamed for clarity

  return (
    <div className="w-64 min-h-screen bg-slate-950 text-white p-4 space-y-2">
      <nav className="flex flex-col gap-2">

        {/* Dashboard Section */}
        <button
          className="w-full text-left flex items-center justify-between px-3 py-2 rounded hover:bg-slate-800 transition"
          onClick={() => setIsDashboardOpen(!isDashboardOpen)}
        >
          <div className="flex items-center gap-2">
            <LayoutDashboard size={18} />
            <span className="text-sm">Dashboard</span>
          </div>
          <ChevronDown className={`transition-transform ${isDashboardOpen ? "rotate-180" : ""}`} size={16} />
        </button>
        {isDashboardOpen && (
          <div className="ml-6 space-y-1">
            <button
              className="block text-sm hover:text-blue-400"
              onClick={() => setActiveComponent("Dashboard")}
            >
              Overview
            </button>
            <button className="block text-sm hover:text-blue-400">Analytic</button>
            <button className="block text-sm hover:text-blue-400">Saas</button>
          </div>
        )}

        {/* Physical Components Section */}
        <button
          className="w-full text-left flex items-center justify-between px-3 py-2 rounded hover:bg-slate-800 transition"
          onClick={() => setIsPhysicalComponentsOpen(!isPhysicalComponentsOpen)}
        >
          <div className="flex items-center gap-2">
            <Layers size={18} />
            <span className="text-sm">Physical Assets</span>
          </div>
          <ChevronDown className={`transition-transform ${isPhysicalComponentsOpen ? "rotate-180" : ""}`} size={16} />
        </button>
        {isPhysicalComponentsOpen && (
          <div className="ml-6 space-y-1">
            <button
              className="block text-sm hover:text-blue-400"
              onClick={() => {
                setActiveComponent("EditVehicle");
                setIsFuelDetailsOpen(!isFuelDetailsOpen);
              }}
            >
              üöó Vehicles
            </button>

            {isFuelDetailsOpen && (
              <div className="ml-4">
                <button className="block text-sm hover:text-blue-400">Fuel Consumption</button>
              </div>
            )}

            <button
              className="block text-sm hover:text-blue-400"
              onClick={() => setActiveComponent("EditInventory")}
            >
              üè™ Inventory
            </button>
          </div>
        )}

        {/* Static Link */}
        <button
          className="w-full text-left flex items-center gap-2 px-3 py-2 rounded hover:bg-slate-800 transition"
          onClick={() => setActiveComponent("Installation")}
        >
          <FileText size={18} />
          <span className="text-sm">Installation</span>
        </button>
      </nav>
    </div>
  );
}

export default Sidebar;
