import { useState } from "react";

function ContainerEntryForm() {
  const [formData, setFormData] = useState({
    consignee: "",
    poNo: "",
    supplier: "",
    material: "",
    arrivalDate: "",
    arrivalTime: "",
    // tax: "",
    type: "",
    containerNo: "",
    inBound: "",
    emptyDate: "",
    outBound: "",
    unloadedAtDock: "",
    emptyAt: "",
    note: ""
  });

  const [documents, setDocuments] = useState([]);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({ ...prev, [name]: value }));
  };

  const handleDocumentChange = (e, index) => {
    const file = e.target.files[0];
    if (!file) return;

    const reader = new FileReader();
    reader.onloadend = () => {
      const updated = [...documents];
      updated[index] = { file, previewUrl: reader.result };
      setDocuments(updated);
    };

    if (file.type.startsWith("image/") || file.type === "application/pdf") {
      reader.readAsDataURL(file);
    } else {
      const updated = [...documents];
      updated[index] = { file, previewUrl: null };
      setDocuments(updated);
    }
  };

  const addDocumentInput = () => {
    setDocuments([...documents, { file: null, previewUrl: null }]);
  };

  const removeDocumentInput = (index) => {
    setDocuments(docs => docs.filter((_, i) => i !== index));
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    console.log("Form Data:", formData);
    console.log("Uploaded Files:", documents.map(d => d.file));
    // Add submission logic here
  };

  const fields = [
    { label: "Container No", name: "containerNo", type: "text" },
    { label: "Type", name: "type", type: "text" },
    { label: "Arrival Date", name: "arrivalDate", type: "date" },
    
    { label: "PO No", name: "poNo", type: "text" },
    { label: "Supplier", name: "supplier", type: "select" },
    { label: "Consignee", name: "consignee", type: "select" },
    { label: "Material", name: "material", type: "textarea" },
    
   
    // { label: "Tax", name: "tax", type: "text" },
    { label: "Status", name: "arrivalTime", type: "select" },
    
    { label: "In Bound", name: "inBound", type: "date" },
    { label: "Empty Date", name: "emptyDate", type: "date" },
    { label: "Out Bound", name: "outBound", type: "date" },
    { label: "Unloaded @ Dock", name: "unloadedAtDock", type: "date" },
    { label: "Empty At", name: "emptyAt", type: "select" }
  ];

  return (
    
    
      <div className="relative h-[100vh] overflow-y-auto pl-2 bg-red-500">
        <form onSubmit={handleSubmit} className="grid grid-cols-1 md:grid-cols-3 gap-1">


          {fields.map(({ label, name, type }) => (
          <div
              key={name}
              className={name === "material" ? "col-span-full md:col-span-3" : ""}
          >
              <label htmlFor={name} className="block mb-1 font-medium text-gray-700">{label}</label>
              {type === "select" ? (
              <select
                  id={name}
                  name={name}
                  value={formData[name]}
                  onChange={handleChange}
                  className="w-full border rounded px-3 py-2 text-base"
              >
                  <option value="">Select {label}</option>
              </select>
              ) : type === "textarea" ? (
              <textarea
                  id={name}
                  name={name}
                  value={formData[name]}
                  onChange={handleChange}
                  rows={3}
                  className="w-full border rounded px-3 py-2 resize-none text-base"
              />
              ) : (
              <input
                  id={name}
                  type={type}
                  name={name}
                  value={formData[name]}
                  onChange={handleChange}
                  className="w-full border rounded px-3 py-2 text-base"
              />
              )}
          </div>
          ))}


          
          <div className="col-span-full">
            <label htmlFor="note" className="block mb-1 font-medium">Personal Note</label>
            <textarea
              id="note"
              name="note"
              value={formData.note}
              onChange={handleChange}
              className="w-full border rounded px-2 py-1.5 "
              rows={2}
            />
          </div>

          
          <div className="col-span-full">
            <label className="block mb-2 font-semibold">Documents</label>
            <div className="grid md:grid-cols-2 lg:grid-cols-3 gap-3">
              {documents.map((doc, idx) => (
                <div key={idx} className="border rounded p-3 relative bg-gray-50 text-sm">
                  <div className="flex justify-between items-center mb-1">
                    <span className="truncate w-4/5">{doc.file?.name || "No file selected"}</span>
                    <span className="text-xs bg-blue-100 text-blue-700 px-2 py-0.5 rounded">
                      {doc.file?.type?.split("/")[1] || "file"}
                    </span>
                  </div>

                  <div className="mb-2">
                    {doc.previewUrl ? (
                      doc.file.type === "application/pdf" ? (
                        <iframe src={doc.previewUrl} className="w-full h-36 border rounded" title={`doc-${idx}`} />
                      ) : (
                        <img src={doc.previewUrl} alt="preview" className="w-full h-36 object-contain border rounded" />
                      )
                    ) : (
                      <div className="w-full h-36 flex items-center justify-center bg-gray-100 border rounded text-gray-500">
                        No Preview
                      </div>
                    )}
                  </div>

                  <input
                    type="file"
                    accept="application/pdf,image/*"
                    onChange={(e) => handleDocumentChange(e, idx)}
                    className="text-xs w-full mb-1"
                  />

                  <button
                    type="button"
                    onClick={() => removeDocumentInput(idx)}
                    className="absolute top-2 right-2 text-red-600 hover:text-red-800 text-base"
                    title="Remove"
                  >
                    ✕
                  </button>
                </div>
              ))}
            </div>

            <button
              type="button"
              onClick={addDocumentInput}
              className="mt-3 bg-green-600 text-white px-3 py-1.5 rounded hover:bg-green-700 text-sm"
            >
              + Add Document
            </button>
          </div>

        
          <div className="col-span-full mt-3">
            <button
              type="submit"
              className="w-full bg-blue-600 text-white py-2 rounded hover:bg-blue-700 text-sm"
            >
              Submit
            </button>
          </div>
        </form>
      </div>
    

    // <div className="max-w-7xl mx-auto pr-4 pl-4 rounded shadow border border-gray-200 text-sm overflow-y-auto ">

    // {/* </div> */}
  );
}

export default ContainerEntryForm;
