import { Routes, Route, useLocation } from "react-router-dom";
import { useState, useEffect } from "react";
import Sidebar from './MenuPanel/Menu';
import ContainerEntry from './Pages/DataEntry/ConatinerEntry';
import LoginPage from './Pages/Login/Login';
import PrivateRoute from './PrivateRoute';
import Dashboard from './Pages/Dashboard/dashboard';
import Report from "./Pages/Report/Report";

export default function MainPage() {
  const location = useLocation();
  const isLoginPage = location.pathname === "/";
  const [sidebarOpen, setSidebarOpen] = useState(false);

  // Disable background scroll on mobile when sidebar is open
  useEffect(() => {
    document.body.classList.toggle("overflow-hidden", sidebarOpen);
  }, [sidebarOpen]);

  return (
    <div className="flex min-h-screen relative bg-slate-100">

      {/* Sidebar */}
      {!isLoginPage && (
        <>
<aside
  className={`fixed inset-y-0 left-0 z-50 w-64 transform bg-slate-950 text-white transition-transform duration-300 ease-in-out
  ${sidebarOpen ? "translate-x-0" : "-translate-x-full"} md:translate-x-0 md:relative md:block`}
>
            <Sidebar onLinkClick={() => setSidebarOpen(false)} />
          </aside>

          {/* Mobile Overlay */}
          {sidebarOpen && (
            <div
              className="fixed inset-0 bg-black bg-opacity-40 z-40 md:hidden"
              onClick={() => setSidebarOpen(false)}
            />
          )}
        </>
      )}

      {/* Main Content */}
      <main className={`flex-1 ${!isLoginPage ? "md:ml-64" : ""}`}>
        {/* Hamburger Toggle for Mobile */}
        {!isLoginPage && (
          <button
            className="md:hidden fixed top-4 left-4 z-50 bg-slate-800 text-white p-2 rounded-md"
            onClick={() => setSidebarOpen(prev => !prev)}
            aria-label="Toggle Sidebar"
          >
            â˜°
          </button>
        )}

        {/* Routes */}
        <div className="p-4 pt-16 md:pt-4">
          <Routes>
            <Route path="/" element={<LoginPage />} />

            <Route
              path="/dashboard"
              element={
                <PrivateRoute>
                  <Dashboard />
                </PrivateRoute>
              }
            />

            <Route
              path="/viewContainer"
              element={
                <PrivateRoute>
                  <ContainerEntry />
                </PrivateRoute>
              }
            />

            <Route
              path="/Report"
              element={
                <PrivateRoute>
                  <Report />
                </PrivateRoute>
              }
            />
          </Routes>
        </div>
      </main>
    </div>
  );
}
