import { useRef, useEffect, useState } from "react";
import axios from "axios";
import TypableSelect from "../../UI/UXComponent/TypebleSelect";
async function getOption(path) {
  try {
    const response = await axios.get(
      `http://${process.env.REACT_APP_NETWORK}:${process.env.REACT_APP_PORT}/${path}`, 
      {
        headers: {
          Authorization: `Bearer ${localStorage.getItem("token")}`
        }
      }
    );

    let data = response.data;
    // console.log(`Fetched API data for ${path}:`, response.data);

    if (typeof data === "string") {
      data = JSON.parse(data);
    }    // Map the nested arrays [id, name] to objects
    return data.data.map(([id, name]) => ({ id, name }));
  } catch (error) {
    console.error(`Failed to fetch ${path}:`, error);
    return [];
  }
}


function ContainerEntryForm() {
  const [formData, setFormData] = useState({
    consignee: "",
    poNo: "",
    supplier: "",
    shippingType: "",
    material: "",
    arrivalDate: "",
    arrivalTime: "",
    type: "",
    containerNo: "",
    inBound: "",
    emptyDate: "",
    outBound: "",
    unloadedAtDock: "",
    emptyAt: "",
    note: ""
  });

  const [documents, setDocuments] = useState([]);
  const fileInputRef = useRef(null);

  const [suppliers, setSuppliers] = useState([]);
  const [consignees, setConsignees] = useState([]);
  const [emptyLocations, setEmptyLocations] = useState([]);
  const [status, setStatus] = useState([]);
  const [type, setType] = useState([]);
  const [shipping, setShipping] = useState([]);

  useEffect(() => {
    async function fetchAllOptions() {
      setSuppliers(await getOption("supplierDetails"));
      // console.log("Fetched suppliers:", suppliers);
      setConsignees(await getOption("consignee"));
      setEmptyLocations(await getOption("unloadVenue"));
      setStatus(await getOption("status"));
      setType(await getOption("containerType"));
      setShipping(await getOption("shippingDocument"));
    }
    fetchAllOptions();
  }, []);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData((prev) => ({ ...prev, [name]: value }));
  };

  const handleAddNewFile = (e) => {
    const file = e.target.files[0];
    if (!file) return;
    setDocuments((prev) => [...prev, { file }]);
    e.target.value = "";
  };

  const triggerFilePicker = () => {
    fileInputRef.current?.click();
  };

  const removeDocumentInput = (index) => {
    setDocuments((docs) => docs.filter((_, i) => i !== index));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    const payload = new FormData();

    Object.entries(formData).forEach(([key, value]) => {
      if (value !== "" && value !== null && value !== undefined) {
        payload.append(key, value);
      }
    });

    documents.forEach((doc) => {
      if (doc.file) {
        payload.append("documents", doc.file);
      }
    });

    try {
      const response = await axios.post(
        `http://${process.env.REACT_APP_NETWORK}:${process.env.REACT_APP_PORT}/setContainerDetails`,
        payload,
        {
          headers: {
            Authorization: `Bearer ${localStorage.getItem("token")}`,
            "Content-Type": "multipart/form-data"
          }
        }
      );

      console.log("Submitted successfully:", response.data);
      alert("Submitted successfully");
    } catch (error) {
      console.error("Submission error:", error);
      alert("Submission failed");
    }
  };



  const fields = [
    { label: "Container No", name: "containerNo", type: "text" },
    { label: "Type", name: "type", type: "select", options: type },
    { label: "Arrival Date", name: "arrivalDate", type: "date" },
    { label: "PO No", name: "poNo", type: "text" },
    { label: "Supplier", name: "supplier", type: "select", options: suppliers },
    { label: "Consignee", name: "consignee", type: "select", options: consignees },
    { label: "Shipping Type", name: "shippingType", type: "select", options: shipping },
    { label: "Material", name: "material", type: "textarea" },
    { label: "Status", name: "Status", type: "select", options: status },
    { label: "In Bound", name: "inBound", type: "date" },
    { label: "Empty Date", name: "emptyDate", type: "date" },
    { label: "Out Bound", name: "outBound", type: "date" },
    { label: "Unloaded @ Dock", name: "unloadedAtDock", type: "date" },
    { label: "Empty At", name: "emptyAt", type: "select", options: emptyLocations }
  ];

  return (
    <form onSubmit={handleSubmit} className="grid grid-cols-1 md:grid-cols-3 gap-4 p-4">

      {fields.map(({ label, name, type, options }) => (
        <div key={name} className={name === "material" ? "col-span-full md:col-span-3" : ""}>
          <label htmlFor={name} className="block mb-1 font-medium text-gray-700">{label}</label>
          {type === "select" ? (
            <TypableSelect
              options={options}
              value={formData[name]}
              onChange={(val) => setFormData(prev => ({ ...prev, [name]: val }))}
              placeholder={`Select ${label}`}
              disabled={options.length === 0}
            />
          ) : type === "textarea" ? (
            <textarea
              id={name}
              name={name}
              value={formData[name]}
              onChange={handleChange}
              rows={3}
              className="w-full border rounded px-3 py-2 resize-none text-base"
            />
          ) : (
            <input
              id={name}
              type={type}
              name={name}
              value={formData[name]}
              onChange={handleChange}
              onFocus={(e) => {
                if (type === "date" && e.target.showPicker) e.target.showPicker();
              }}
              className="w-full border rounded px-3 py-2 text-base"
            />
          )}
        </div>
      ))}

      {/* Note */}
      <div className="col-span-full">
        <label htmlFor="note" className="block mb-1 font-medium">Personal Note</label>
        <textarea
          id="note"
          name="note"
          value={formData.note}
          onChange={handleChange}
          className="w-full border rounded px-2 py-1.5"
          rows={2}
        />
      </div>

      {/* Documents */}
      <div className="col-span-full">
        <label className="block mb-2 font-semibold">Documents</label>
        <div className="grid md:grid-cols-2 lg:grid-cols-3 gap-3">
          {documents.map((doc, idx) => (
            <div key={idx} className="border rounded p-3 relative bg-gray-50 text-sm shadow-sm">
              <div className="flex justify-between items-center mb-2">
                {doc.file ? (
                  <a
                    href={URL.createObjectURL(doc.file)}
                    target="_blank"
                    rel="noopener noreferrer"
                    className="truncate w-4/5 text-blue-600 hover:underline"
                    title="Click to open"
                  >
                    {doc.file.name}
                  </a>
                ) : (
                  <span className="truncate w-4/5">No file selected</span>
                )}
                <span className="text-xs bg-blue-100 text-blue-700 px-2 py-0.5 rounded">
                  {doc.file?.type?.split("/")[1] || "file"}
                </span>
              </div>
              <button
                type="button"
                onClick={() => removeDocumentInput(idx)}
                className="absolute top-2 right-2 text-red-600 hover:text-red-800 text-base"
                title="Remove"
              >
                ✕
              </button>
            </div>
          ))}
        </div>

        <input
          type="file"
          ref={fileInputRef}
          accept="application/pdf,image/*"
          onChange={handleAddNewFile}
          className="hidden"
        />

        <div className="mt-3 flex justify-end">
          <button
            type="button"
            onClick={triggerFilePicker}
            className="bg-green-600 text-white px-3 py-1.5 rounded hover:bg-green-700 text-sm"
          >
            + Add Document
          </button>
        </div>
      </div>

      {/* Submit */}
      <div className="col-span-full mt-3">
        <button
          type="submit"
          className="w-full bg-blue-600 text-white py-2 rounded hover:bg-blue-700 text-sm"
        >
          Submit
        </button>
      </div>
    </form>
  );
}

export default ContainerEntryForm;
