import { useEffect, useState } from "react";
import axios from "axios";

function Report({ onSubmitSuccess }) {
  const [products, setProducts] = useState([
    { name: "", quantity: "", reason: "", images: [], videos: [] }
  ]);
  const [containers, setContainers] = useState([]);
  const [selectedContainer, setSelectedContainer] = useState("");

  useEffect(() => {
    const fetchContainers = async () => {
      try {
        const response = await axios.get(
          `http://${process.env.REACT_APP_NETWORK}:${process.env.REACT_APP_PORT}/getAllContainers`,
          {
            headers: {
              Authorization: `Bearer ${localStorage.getItem("token")}`,
            },
          }
        );
        setContainers(response.data);
      } catch (err) {
        console.error("Error fetching containers:", err);
      }
    };
    fetchContainers();
  }, []);

  const handleChange = (index, field, value) => {
    const updated = [...products];
    updated[index][field] = value;
    setProducts(updated);
  };

  const addProduct = () => {
    setProducts([...products, { name: "", quantity: "", reason: "", images: [], videos: [] }]);
  };

  const removeProduct = (index) => {
    setProducts(products.filter((_, i) => i !== index));
  };

  const addMedia = (index, files, type) => {
    const updated = [...products];
    const mediaArray = Array.from(files);
    if (type === "image") {
      updated[index].images.push(...mediaArray);
    } else if (type === "video") {
      updated[index].videos.push(...mediaArray);
    }
    setProducts(updated);
  };

  const removeMedia = (index, mediaIndex, type) => {
    const updated = [...products];
    if (type === "image") {
      updated[index].images.splice(mediaIndex, 1);
    } else if (type === "video") {
      updated[index].videos.splice(mediaIndex, 1);
    }
    setProducts(updated);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!selectedContainer) {
      alert("Please select a container first.");
      return;
    }

    const formData = new FormData();
    formData.append("container_id", selectedContainer);

    products.forEach((product, i) => {
      formData.append(`products[${i}][name]`, product.name);
      formData.append(`products[${i}][quantity]`, product.quantity);
      formData.append(`products[${i}][reason]`, product.reason);
      product.images.forEach((file) => {
        formData.append(`products[${i}][images]`, file);
      });
      product.videos.forEach((file) => {
        formData.append(`products[${i}][videos]`, file);
      });
    });

    try {
      await axios.post(
        `http://${process.env.REACT_APP_NETWORK}:${process.env.REACT_APP_PORT}/submitDamagedProducts`,
        formData,
        {
          headers: {
            "Content-Type": "multipart/form-data",
            Authorization: `Bearer ${localStorage.getItem("token")}`,
          },
        }
      );
      alert("Damage report submitted!");
      if (onSubmitSuccess) onSubmitSuccess();
    } catch (error) {
      console.error("Submission failed:", error);
      alert("Failed to submit damage report.");
    }
  };

  return (
    <form onSubmit={handleSubmit} className="space-y-6 p-4 max-w-4xl mx-auto">
      <h2 className="text-2xl font-bold text-red-600 mb-4">Damaged Product Report</h2>

      {/* Container Select */}
      <div>
        <label className="block font-medium mb-1">Select Container</label>
        <select
          value={selectedContainer}
          onChange={(e) => setSelectedContainer(e.target.value)}
          className="w-full border px-3 py-2 rounded"
          required
        >
          <option value="">-- Select a container --</option>
          {containers.map((c) => (
            <option key={c.container_id} value={c.container_id}>
              {c.container_no}
            </option>
          ))}
        </select>
      </div>

      {/* Product Inputs */}
      {products.map((product, index) => (
        <div key={index} className="bg-red-50 p-4 rounded shadow-sm space-y-4">
          <input
            type="text"
            placeholder="Product Name"
            value={product.name}
            onChange={(e) => handleChange(index, "name", e.target.value)}
            className="border px-2 py-1 rounded w-full"
            required
          />
          <input
            type="number"
            placeholder="Quantity"
            value={product.quantity}
            onChange={(e) => handleChange(index, "quantity", e.target.value)}
            className="border px-2 py-1 rounded w-full"
            required
          />
          <input
            type="text"
            placeholder="Reason"
            value={product.reason}
            onChange={(e) => handleChange(index, "reason", e.target.value)}
            className="border px-2 py-1 rounded w-full"
          />

          {/* Image Upload */}
          <div>
            <label className="block font-medium">Photos</label>
            <div className="flex flex-wrap gap-2 mb-2">
              {product.images.map((img, i) => (
                <div key={i} className="relative w-20 h-20">
                  <img
                    src={URL.createObjectURL(img)}
                    alt="preview"
                    className="w-full h-full object-cover rounded border"
                  />
                  <button
                    type="button"
                    onClick={() => removeMedia(index, i, "image")}
                    className="absolute top-0 right-0 bg-black text-white text-xs px-1 rounded"
                  >
                    ✕
                  </button>
                </div>
              ))}
            </div>
            <input
              type="file"
              accept="image/*"
              capture="environment"
              multiple
              onChange={(e) => addMedia(index, e.target.files, "image")}
              className="border px-2 py-1 rounded w-full"
            />
            <p className="text-xs text-gray-600 mt-1">
              Tap to take or select multiple images.
            </p>
          </div>

          {/* Video Upload */}
          <div>
            <label className="block font-medium">Videos</label>
            <div className="flex flex-wrap gap-2 mb-2">
              {product.videos.map((vid, i) => (
                <div key={i} className="relative w-24">
                  <video
                    src={URL.createObjectURL(vid)}
                    controls
                    className="w-full rounded border"
                  />
                  <button
                    type="button"
                    onClick={() => removeMedia(index, i, "video")}
                    className="absolute top-0 right-0 bg-black text-white text-xs px-1 rounded"
                  >
                    ✕
                  </button>
                </div>
              ))}
            </div>
            <input
              type="file"
              accept="video/*"
              capture="environment"
              multiple
              onChange={(e) => addMedia(index, e.target.files, "video")}
              className="border px-2 py-1 rounded w-full"
            />
            <p className="text-xs text-gray-600 mt-1">
              Tap to record or select multiple videos.
            </p>
          </div>

          <div className="flex justify-end">
            <button
              type="button"
              onClick={() => removeProduct(index)}
              className="text-red-500 hover:text-red-700"
            >
              ✕ Remove Product
            </button>
          </div>
        </div>
      ))}

      {/* Buttons */}
      <div className="flex justify-between items-center">
        <button
          type="button"
          onClick={addProduct}
          className="bg-blue-600 text-white px-3 py-1.5 rounded hover:bg-blue-700"
        >
          + Add Product
        </button>
        <button
          type="submit"
          className="bg-green-600 text-white px-4 py-2 rounded hover:bg-green-700"
        >
          Submit Report
        </button>
      </div>
    </form>
  );
}

export default Report;
