import { Routes, Route, useLocation } from "react-router-dom";
import { useState, useEffect } from "react";
import Sidebar from './MenuPanel/Menu';
// import ContainerEntry from './Pages/DataEntry/ContainerEntry'; // ✅ Fixed typo

import LoginPage from './Pages/Login/Login';
import PrivateRoute from './PrivateRoute';
import Dashboard from './Pages/Dashboard/dashboard';
import ContainerForReport from './Pages/Report/Report';

export default function MainPage() {
  const location = useLocation();
  const isLoginPage = location.pathname === "/";
  const [sidebarVisible, setSidebarVisible] = useState(false);

  useEffect(() => {
    document.body.classList.toggle("overflow-hidden", sidebarVisible);
    return () => document.body.classList.remove("overflow-hidden");
  }, [sidebarVisible]);

  return (
    <div className="flex min-h-screen bg-slate-100 relative">

      {/* Desktop Sidebar */}
      {!isLoginPage && (
        <aside className="hidden md:block bg-slate-950 z-30 w-64">
          <Sidebar />
        </aside>
      )}

      {/* Mobile Sidebar Overlay */}
      {sidebarVisible && (
        <div className="fixed inset-0 z-50 flex md:hidden">
          <div className="w-64 bg-slate-950">
            <Sidebar onLinkClick={() => setSidebarVisible(false)} />
          </div>
          <div
            className="flex-1 bg-black bg-opacity-50"
            onClick={() => setSidebarVisible(false)}
            aria-hidden="true"
            role="presentation"
          />
        </div>
      )}

      {/* Main Content */}
      <main className={`flex-1 w-full ${!isLoginPage ? "md:ml-64" : ""}`}>

        {/* Mobile Menu Button in Flow */}
        {!isLoginPage && (
          <div className="md:hidden px-4 pt-4">
            <button
              className="bg-slate-800 text-white p-2 rounded shadow"
              onClick={() => setSidebarVisible(true)}
              aria-label="Toggle Sidebar"
            >
              ☰
            </button>
          </div>
        )}

        {/* Route Container */}
        <div className="pt-4 px-4 sm:px-6 md:px-8">
          <Routes>
            <Route path="/" element={<LoginPage />} />
            <Route path="/dashboard" element={<PrivateRoute><Dashboard /></PrivateRoute>} />
            <Route path="/viewContainer" element={<PrivateRoute><ContainerEntry /></PrivateRoute>} />
            <Route path="/report" element={<PrivateRoute><ContainerForReport /></PrivateRoute>} />
          </Routes>
        </div>

      </main>
    </div>
  );
}
