// src/utils/api.js
import axios from "axios";

const API_URL = "http://localhost:8000";
const API_URL1 = `${process.env.REACT_APP_NETWORK}:${process.env.REACT_APP_PORT}`;
export const loginUser = async (username, password) => {
  const response = await axios.post(`${process.env.REACT_APP_NETWORK}:${process.env.REACT_APP_PORT}/token`, new URLSearchParams({
    username,
    password
  }));
  return response.data;
};

export const fetchProtectedData = async (token) => {
  const response = await axios.get(`${API_URL1}/users/me`, {
    headers: {
      Authorization: `Bearer ${token}`
    }
  });
  return response.data;
};
