import React, { createContext, useContext, useState } from "react";

// Create Context
const AuthContext = createContext();

export const AuthProvider = ({ children }) => {
  const [token, setToken] = useState(localStorage.getItem("token") || null);
  const [refreshToken, setRefreshToken] = useState(localStorage.getItem("refreshToken") || null);

  // Login function
  const login = (accessToken, refresh) => {
    localStorage.setItem("token", accessToken);
    localStorage.setItem("refreshToken", refresh);
    setToken(accessToken);
    setRefreshToken(refresh);
  };

  // Logout function
  const logout = () => {
    localStorage.removeItem("token");
    localStorage.removeItem("refreshToken");
    setToken(null);
    setRefreshToken(null);
  };

  // Function to refresh the access token using the refresh token
    const refreshAccessToken = async () => {
    if (!refreshToken) return null;

    try {
        const response = await fetch(`http://${process.env.REACT_APP_NETWORK}:${process.env.REACT_APP_PORT}/refresh`, {
        method: "POST",
        headers: {
            "Content-Type": "application/json",
        },
        body: JSON.stringify({ refresh_token: refreshToken }),
        });

        if (!response.ok) {
        logout(); // Clean up if refresh fails
        return null;
        }

        const data = await response.json();
        const newAccessToken = data.access_token;

        localStorage.setItem("token", newAccessToken);
        setToken(newAccessToken);
        return newAccessToken;

    } catch (err) {
        console.error("Refresh token error:", err);
        logout();
        return null;
    }
    };


  return (
    <AuthContext.Provider value={{ token, refreshAccessToken, login, logout }}>
      {children}
    </AuthContext.Provider>
  );
};

export const useAuth = () => {
  return useContext(AuthContext);
};
