import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import {
  LayoutDashboard,
  FileText,
  Container,
  ChevronDown
} from 'lucide-react';
// import { Tooltip as ReactTooltip } from 'react-tooltip';
// import 'react-tooltip/dist/react-tooltip.css';

function Sidebar({ onLinkClick }) {
  const [isDashboardOpen, setIsDashboardOpen] = useState(false);

  return (
    <div className="hidden md:group md:relative md:flex md:w-16 md:hover:w-64 transition-all duration-300 min-h-screen bg-slate-950 text-white flex-col justify-between overflow-hidden">

      {/* Navigation */}
      <div className="p-4 space-y-2">
        <nav className="flex flex-col gap-2">

          {/* Dashboard */}
          <div className="group relative">
            <button
              onClick={() => setIsDashboardOpen(!isDashboardOpen)}
              className="w-full flex items-center gap-3 px-3 py-2 rounded hover:bg-slate-800 transition"
            >
              <LayoutDashboard size={18} />
              <span className="text-sm group-hover:inline hidden">Dashboard</span>
              <ChevronDown
                className={`ml-auto transition-transform ${isDashboardOpen ? 'rotate-180' : ''}`}
                size={16}
              />
            </button>
            {isDashboardOpen && (
              <div className="ml-6 mt-1 space-y-1">
                <Link to="/dashboard" className="block text-sm hover:text-blue-400" onClick={onLinkClick}>
                  Overview
                </Link>
                <Link to="/dashboard/analytics" className="block text-sm hover:text-blue-400" onClick={onLinkClick}>
                  Analytics
                </Link>
                <Link to="/dashboard/saas" className="block text-sm hover:text-blue-400" onClick={onLinkClick}>
                  SaaS
                </Link>
              </div>
            )}
          </div>

          {/* Report */}
          <Link
            to="/report"
            data-tip="Report"
            onClick={onLinkClick}
            className="w-full flex items-center gap-3 px-3 py-2 rounded hover:bg-slate-800 transition"
          >
            <FileText size={18} />
            <span className="text-sm group-hover:inline hidden">Report</span>
          </Link>

          {/* View Container */}
          <Link
            to="/viewContainer"
            data-tip="View Container"
            onClick={onLinkClick}
            className="w-full flex items-center gap-3 px-3 py-2 rounded hover:bg-slate-800 transition"
          >
            <Container size={18} />
            <span className="text-sm group-hover:inline hidden">View Container</span>
          </Link>
        </nav>
      </div>

      {/* User Profile */}
      <div className="w-full px-4 py-3 border-t border-slate-800 flex items-center gap-3 bg-slate-950">
        <img
          src="/user.jpg"
          alt="User"
          className="w-10 h-10 rounded-full object-cover"
        />
        <div className="group-hover:block hidden">
          <p className="text-sm font-medium">John Doe</p>
          <p className="text-xs text-slate-400">Logged in</p>
        </div>
      </div>

      {/* Tooltip (Optional) */}
      {/* <ReactTooltip place="right" type="dark" effect="solid" /> */}
    </div>
  );
}

export default Sidebar;
