import { useState } from "react";

function ContainerEntryForm() {
  const [formData, setFormData] = useState({
    consignee: "",
    poNo: "",
    supplier: "",
    material: "",
    arrivalDate: "",
    arrivalTime: "",
    // tax: "",
    type: "",
    containerNo: "",
    inBound: "",
    emptyDate: "",
    outBound: "",
    unloadedAtDock: "",
    emptyAt: "",
    note: ""
  });

  const [documents, setDocuments] = useState([]);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({ ...prev, [name]: value }));
  };

  const handleDocumentChange = (e, index) => {
    const file = e.target.files[0];
    if (!file) return;

    const reader = new FileReader();
    reader.onloadend = () => {
      const updated = [...documents];
      updated[index] = { file, previewUrl: reader.result };
      setDocuments(updated);
    };

    if (file.type.startsWith("image/") || file.type === "application/pdf") {
      reader.readAsDataURL(file);
    } else {
      const updated = [...documents];
      updated[index] = { file, previewUrl: null };
      setDocuments(updated);
    }
  };

  const addDocumentInput = () => {
    setDocuments([...documents, { file: null, previewUrl: null }]);
  };

  const removeDocumentInput = (index) => {
    setDocuments(docs => docs.filter((_, i) => i !== index));
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    console.log("Form Data:", formData);
    console.log("Uploaded Files:", documents.map(d => d.file));
    // Add submission logic here
  };

  const fields = [
    { label: "Container No", name: "containerNo", type: "text" },
    { label: "Type", name: "type", type: "text" },
    { label: "Arrival Date", name: "arrivalDate", type: "date" },
    
    { label: "PO No", name: "poNo", type: "text" },
    { label: "Supplier", name: "supplier", type: "select" },
    { label: "Consignee", name: "consignee", type: "select" },
    { label: "Material", name: "material", type: "textarea" },
    
   
    // { label: "Tax", name: "tax", type: "text" },
    { label: "Status", name: "arrivalTime", type: "select" },
    
    { label: "In Bound", name: "inBound", type: "date" },
    { label: "Empty Date", name: "emptyDate", type: "date" },
    { label: "Out Bound", name: "outBound", type: "date" },
    { label: "Unloaded @ Dock", name: "unloadedAtDock", type: "date" },
    { label: "Empty At", name: "emptyAt", type: "select" }
  ];

  return (
    
    <div className="flex flex-col h-full">
      <div className="flex-1 overflow-y-auto p-4">
        <form onSubmit={handleSubmit} className="grid grid-cols-1 md:grid-cols-3 gap-1">

        </form>
      </div>
    </div>

    // <div className="max-w-7xl mx-auto pr-4 pl-4 rounded shadow border border-gray-200 text-sm overflow-y-auto ">

    // {/* </div> */}
  );
}

export default ContainerEntryForm;
