import { useEffect, useState } from "react";
import { Camera, Video, Upload, Trash2 } from "lucide-react";
import axios from "axios";
import TypableSelect from "../../UI/UXComponent/TypebleSelect";

function ReportForm({ editData, onSubmitSuccess }) {
  const [products, setProducts] = useState([
    { name: "", quantity: "", reason: "", files: [], previews: [] },
  ]);
  const [containers, setContainers] = useState([]);
  const [selectedContainer, setSelectedContainer] = useState(null);
  const [originalData, setOriginalData] = useState(null);
  const [appendedContainerId, setAppendedContainerId] = useState(null);

  useEffect(() => {
    const fetchContainers = async () => {
      try {
        const response = await axios.get(
          `http://${process.env.REACT_APP_NETWORK}:${process.env.REACT_APP_PORT}/getAllContainers`,
          {
            headers: {
              Authorization: `Bearer ${localStorage.getItem("token")}`,
            },
          }
        );

        const data = JSON.parse(response.data).data;
        if (Array.isArray(data)) {
          setContainers(data);
        } else {
          setContainers([]);
        }
      } catch (err) {
        console.error("Error fetching containers:", err);
      }
    };

    fetchContainers();
  }, []);

  useEffect(() => {
    if (!editData || containers.length === 0) return;

    const containerId = editData.containerId;
    const containerNumber = editData.container_number;

    const matched = containers.find(([id]) => id === containerId);
    console.log(matched)
    if (!matched) {
      setContainers((prev) => [...prev, [containerId, containerNumber]]);
      setAppendedContainerId(containerId);
      setSelectedContainer({ id: containerId, name: containerNumber });
    } else {
      setSelectedContainer({ id: matched[0], name: matched[1] });
    }

    const mappedProducts = editData.products.map((product) => ({
      name: product.name || "",
      quantity: product.quantity || "",
      reason: product.reason || "",
      files: [],
      previews:
        product.files?.map((doc) => ({
          id: doc.id,
          name: doc.filename || "file",
          url: `http://${process.env.REACT_APP_NETWORK}:${process.env.REACT_APP_PORT}/getReportImage/${doc.id}`,
          type: doc.filename?.match(/\.(mp4|webm|ogg)$/i) ? "video" : "image",
        })) || [],
    }));

    setProducts(mappedProducts);

    setOriginalData({
      container_id: containerId,
      products: mappedProducts.map(({ name, quantity, reason }) => ({
        name,
        quantity,
        reason,
      })),
    });
    
  }, [editData, containers]); // <- Add full `containers`, not just containers.length


  const isDataChanged = () => {
    if (!originalData) return true;
    if (selectedContainer?.id !== originalData.container_id) return true;
    if (products.length !== originalData.products.length) return true;

    for (let i = 0; i < products.length; i++) {
      const p = products[i];
      const o = originalData.products[i];
      if (
        p.name !== o.name ||
        p.quantity !== o.quantity ||
        p.reason !== o.reason ||
        p.files.length > 0
      )
        return true;
    }
    return false;
  };

  const handleChange = (index, field, value) => {
    const updated = [...products];
    updated[index][field] = value;
    setProducts(updated);
  };

  const handleRemoveFile = (productIndex, fileIndex) => {
    const updated = [...products];
    const removedPreview = updated[productIndex].previews[fileIndex];
    if (removedPreview.url?.startsWith("blob:")) URL.revokeObjectURL(removedPreview.url);
    updated[productIndex].files.splice(fileIndex, 1);
    updated[productIndex].previews.splice(fileIndex, 1);
    setProducts(updated);
  };

  const handleFileChange = (index, files) => {
    const fileArray = Array.from(files);
    const updated = [...products];
    const existingFiles = updated[index].files || [];
    const existingPreviews = updated[index].previews || [];

    updated[index].files = [...existingFiles, ...fileArray];

    const newPreviews = fileArray.map((file) => ({
      url: URL.createObjectURL(file),
      type: file.type.startsWith("image") ? "image" : "video",
      name: file.name,
    }));

    updated[index].previews = [...existingPreviews, ...newPreviews];
    setProducts(updated);
  };

  const addProduct = () => {
    setProducts([...products, { name: "", quantity: "", reason: "", files: [], previews: [] }]);
  };

  const removeProduct = (index) => {
    products[index].previews.forEach((preview) => {
      if (preview.url?.startsWith("blob:")) URL.revokeObjectURL(preview.url);
    });
    setProducts(products.filter((_, i) => i !== index));
  };

  const submitNewReport = async (container, products) => {
    const containerId = container?.id || container;
    if (!containerId) throw new Error("Container ID is required");

    const formData = new FormData();
    const dataPayload = {
      container_id: containerId,
      products: products.map((product) => ({
        name: product.name,
        quantity: product.quantity,
        reason: product.reason,
        files: product.files.map(() => "placeholder"),
      })),
    };

    formData.append("data", JSON.stringify(dataPayload));
    products.forEach((product) => product.files.forEach((file) => formData.append("files", file)));

    const url = `http://${process.env.REACT_APP_NETWORK}:${process.env.REACT_APP_PORT}/submitDamagedProducts`;
    const headers = {
      Authorization: `Bearer ${localStorage.getItem("token")}`,
    };

    return axios.post(url, formData, { headers });
  };

  const submitEditReport = async (container, products) => {
    if (!container || !container.id) throw new Error("Please select a container.");

    const formData = new FormData();
    const changedProducts = products.map((product) => ({
      name: product.name,
      quantity: product.quantity,
      reason: product.reason,
      files: product.files.map(() => "placeholder"),
    }));

    const dataPayload = {
      container_id: container.id,
      products: changedProducts,
      report_id: editData.report_id,
    };

    formData.append("data", JSON.stringify(dataPayload));
    products.forEach((product) => product.files.forEach((file) => formData.append("files", file)));

    const url = `http://${process.env.REACT_APP_NETWORK}:${process.env.REACT_APP_PORT}/updateDamagedProducts/${editData.report_id}`;
    const headers = {
      Authorization: `Bearer ${localStorage.getItem("token")}`,
    };

    return axios.post(url, formData, { headers });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!selectedContainer) {
      alert("Please select a container.");
      return;
    }

    if (editData && !isDataChanged()) {
      alert("No changes detected, nothing to submit.");
      return;
    }

    try {
      if (editData) {
        await submitEditReport(selectedContainer, products);
        alert("Damage report updated!");
      } else {
        await submitNewReport(selectedContainer, products);
        alert("Damage report submitted!");
      }

      if (onSubmitSuccess) onSubmitSuccess();

      if (appendedContainerId) {
        setContainers((prev) => prev.filter(([id]) => id !== appendedContainerId));
        setAppendedContainerId(null);
      }
    } catch (error) {
      console.error("Submission failed:", error);
      alert("Failed to submit damage report.");
    }
  };

  return (
    <form onSubmit={handleSubmit} className="max-w-5xl mx-auto p-4 space-y-6">
      <h2 className="text-3xl font-bold text-red-600 text-center">
        {editData ? "Edit Damaged Product Report" : "New Damaged Product Report"}
      </h2>

      <div>
        <label className="block font-medium mb-2">Select Container</label>
        <TypableSelect
          options={containers.map(([id, number]) => ({ id, name: number }))}
          value={selectedContainer}
          onChange={setSelectedContainer}
          placeholder="Select or type to search"
        />
      </div>

      {products.map((product, index) => (
        <div key={index} className="bg-red-50 border border-red-200 rounded-xl p-4 space-y-4 shadow-sm">
          <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
            <input
              type="text"
              placeholder="Product Name"
              value={product.name}
              onChange={(e) => handleChange(index, "name", e.target.value)}
              className="border px-3 py-2 rounded"
              required
            />
            <input
              type="number"
              placeholder="Quantity"
              value={product.quantity}
              onChange={(e) => handleChange(index, "quantity", e.target.value)}
              className="border px-3 py-2 rounded"
              required
            />
            <input
              type="text"
              placeholder="Reason"
              value={product.reason}
              onChange={(e) => handleChange(index, "reason", e.target.value)}
              className="border px-3 py-2 rounded"
            />
          </div>

          <div className="grid grid-cols-2 sm:grid-cols-3 md:grid-cols-4 gap-4">
            <div className="block sm:hidden">
              <label className="flex items-center gap-2 cursor-pointer">
                <Camera className="w-5 h-5" />
                <span>Take Photo</span>
                <input type="file" accept="image/*" capture="environment" onChange={(e) => handleFileChange(index, e.target.files)} className="hidden" />
              </label>
            </div>
            <div className="block sm:hidden">
              <label className="flex items-center gap-2 cursor-pointer">
                <Video className="w-5 h-5" />
                <span>Take Video</span>
                <input type="file" accept="video/*" capture="environment" onChange={(e) => handleFileChange(index, e.target.files)} className="hidden" />
              </label>
            </div>
            <div className="col-span-2 sm:col-span-1">
              <label className="flex items-center gap-2 cursor-pointer">
                <Upload className="w-5 h-5" />
                <span>Select Files</span>
                <input type="file" accept="image/*,video/*" multiple onChange={(e) => handleFileChange(index, e.target.files)} className="hidden" />
              </label>
              <p className="text-sm text-gray-500">{product.files.length} file(s) selected</p>
            </div>
          </div>

          {product.previews?.length > 0 && (
            <div className="grid grid-cols-1 sm:grid-cols-2 gap-4 mt-4">
              {product.previews.map((preview, i) => (
                <div key={i} className="flex items-center justify-between border rounded p-2">
                  <a href={preview.url} target="_blank" rel="noopener noreferrer" className="text-blue-600 underline break-words max-w-full overflow-hidden text-ellipsis whitespace-nowrap" title={preview.name || `File ${i + 1}`}>
                    {preview.name || `File ${i + 1}`}
                  </a>
                  <button type="button" onClick={() => handleRemoveFile(index, i)} className="ml-4 px-2 py-1 text-black rounded hover:bg-red-700" aria-label="Remove file">
                    <Trash2 className="w-4 h-4" />
                  </button>
                </div>
              ))}
            </div>
          )}

          {products.length > 1 && (
            <button type="button" onClick={() => removeProduct(index)} className="text-red-600 underline">
              Remove Product
            </button>
          )}
        </div>
      ))}

      <button type="button" onClick={addProduct} className="px-4 py-2 rounded bg-red-600 text-white hover:bg-red-700">
        Add Product
      </button>

      <div className="flex justify-center mt-6">
        <button type="submit" className="px-6 py-3 rounded bg-red-600 text-white font-semibold hover:bg-red-700">
          {editData ? "Update Report" : "Submit Report"}
        </button>
      </div>
    </form>
  );
}

export default ReportForm;
