// Sidebar.jsx
import React from "react";
import { Link } from "react-router-dom";
import {
  ChevronDown,
  LayoutDashboard,
  FileText,
  Container,
  X
} from "lucide-react";

function Sidebar({ isOpen, onClose }) {
  const [isDashboardOpen, setIsDashboardOpen] = React.useState(false);

  return (
    <div className={`fixed z-50 top-0 left-0 h-full w-64 bg-slate-950 text-white transition-transform duration-300 ${isOpen ? 'translate-x-0' : '-translate-x-full'}`}>
      <div className="flex justify-between items-center p-4 border-b border-slate-800">
        <h2 className="text-lg font-semibold">Menu</h2>
        <button onClick={onClose}>
          <X size={20} />
        </button>
      </div>

      <nav className="p-4 space-y-2">
        {/* Dashboard */}
        <button
          className="w-full text-left flex items-center justify-between px-3 py-2 rounded hover:bg-slate-800 transition"
          onClick={() => setIsDashboardOpen(!isDashboardOpen)}
        >
          <div className="flex items-center gap-2">
            <LayoutDashboard size={18} />
            <span className="text-sm">Dashboard</span>
          </div>
          <ChevronDown className={`transition-transform ${isDashboardOpen ? "rotate-180" : ""}`} size={16} />
        </button>

        {isDashboardOpen && (
          <div className="ml-6 space-y-1">
            <Link to="/dashboard" className="block text-sm hover:text-blue-400">Overview</Link>
            <button className="block text-sm hover:text-blue-400">Analytic</button>
            <button className="block text-sm hover:text-blue-400">Saas</button>
          </div>
        )}

        <div className="w-full flex items-center gap-2 px-3 py-2 rounded hover:bg-slate-800 transition">
          <FileText size={18} />
          <Link to="/viewContainer" className="text-sm">Report</Link>
        </div>

        <div className="w-full flex items-center gap-2 px-3 py-2 rounded hover:bg-slate-800 transition">
          <Container size={18} />
          <Link to="/viewContainer" className="text-sm">View Container</Link>
        </div>
      </nav>
    </div>
  );
}

export default Sidebar;
